class TodoList {
    constructor() {
        this.todos = [];
    }

    add(task) {
        this.todos.push({ id: Date.now(), task, done: false });
    }

    toggle(id) {
        const todo = this.todos.find(t => t.id === id);
        if (todo) todo.done = !todo.done;
    }

    remove(id) {
        this.todos = this.todos.filter(t => t.id !== id);
    }
}

const list = new TodoList();
list.add("Learn JavaScript");
