def fibonacci(n):
    a, b = 0, 1
    for _ in range(n):
        yield a
        a, b = b, a + b

for num in fibonacci(10):
    print(num)

def infinite_counter():
    count = 0
    while True:
        yield count
        count += 1

counter = infinite_counter()
print([next(counter) for _ in range(5)])
