class Person:
    def __init__(self, name, age):
        self.name = name
        self.age = age

    def greet(self):
        return f"Hi, I'm {self.name}!"

    def birthday(self):
        self.age += 1

alice = Person("Alice", 25)
print(alice.greet())
alice.birthday()
print(f"Age: {alice.age}")
